import React from 'react';
import '../App.css';
import { Button } from 'react-bootstrap';

class InterestTab extends React.Component {

    addTab() {

        for(let i = 0; i < this.props.tabs.length; i++) {

            if (this.props.tabs[i] === this.props.title) {

                this.props.tabs.splice(i, 1);
            }
        }
        this.props.updateTabs(this.props.tabs)
    }

    render() {
        return (
            <div style = {{borderRadius:5,
                borderColor:'grey',
                padding:4, margin:2,
                display:'flex',
                flexDirection:'row'}}>

                <span role="img" aria-label="sheep">➡️
                <Button onClick={() => this.addTab()}></Button></span>
                <span>{this.props.title}</span>
            </div>
        )
    }
}

export default InterestTab;
