import React from 'react';
import '../App.css';
import RecommendedCourse from './RecommendedCourse';

class CourseRecommendor extends React.Component{
  constructor(props){
    super(props);

  }
    getCourses() {
        let courses = [];

        for(const course of Object.entries(this.props.courses)) {
          for(const interests of Object.entries(this.props.interests)) {
            for (let i = 0; i < courses[1].keywords.length; i++) {
              //console.log(course[1].keywords);
              for (let j = 0; j < this.props.interests.length; j++) {
                if(interests === course[1].keywords[i]) {
                  courses.push(
                    <RecommendedCourse coursekey={course[0]} data={course[1]}>

                    </RecommendedCourse>
                  )
                  break;
                }
              }
            }
          }          
        }
        return courses;
      }

      render() {
        return (

          <div class="container-fluid">
            <div class="row">
              <div class="col-lg-6" >{this.getCourses()}</div>
              <div class="col-lg-6" >{this.getCourses()}</div>
            </div>
          </div>

        )
      }
}


export default CourseRecommendor
