import React from 'react';
import '../App.css';
import {Button} from 'react-bootstrap';

class Rating extends React.Component {
  constructor(props) {
      super(props);
      this.state = {
          variant: 'light',
          value: 0
      }
  }
  async changeRating(e, x) {
    //if(this.state.variant === 'light') {
        await this.setState( {variant: 'dark', value: x} );
    //}
    //console.log(this.props.key);
    this.props.setRatedCourses(this.state.value, this.props.key);
  }
    render() {
        return (
            <>
            <Button variant = {this.state.variant} 
                    style={{height: '10%', width: '19%', margin: '1px'}}
                    onClick={ (e) => this.changeRating(e, 1) } >
                    1
            </Button>
            <Button variant = {this.state.variant} 
                    style={{height: '10%', width: '19%', margin: '1px'}}
                    onClick={ (e) => this.changeRating(e, 2) } >
                    2
            </Button>
            <Button variant = {this.state.variant} 
                    style={{height: '10%', width: '19%', margin: '1px'}}
                    onClick={ (e) => this.changeRating(e, 3) } >
                    3
            </Button>
            <Button variant = {this.state.variant} 
                    style={{height: '10%', width: '19%', margin: '1px'}}
                    onClick={ (e) => this.changeRating(e, 4) } >
                    4
            </Button>
            <Button variant = {this.state.variant} 
                    style={{height: '10%', width: '19%', margin: '1px'}}
                    onClick={ (e) => this.changeRating(e, 5) } >
                    5
            </Button>
            </>
        )
      }
}

export default Rating
