import React from 'react';
import '../App.css';
import RecommendedCourse from './RecommendedCourse';

class CourseRecommendor extends React.Component{
  constructor(props){
    super(props);

  }
    getCourses() {
        let courses = [];

        for(const course of Object.entries(this.props.courses)) {
          //console.log(this.props.ratedCourses);
          for(const rated of Object.entries(this.props.ratedCourses)) {
            let keywords = [];
            //console.log(rated[0]);  
            //console.log(course[1]);

            //console.log(course[0] === rated[0]);  

            if (rated[0] === course[0]) {
              if (rated[1] >= 3) {
                keywords = course[1].keywords;
              }
              for (let i = 0; i < keywords.length; i++) {
                console.log(keywords);
                console.log(course[1].keywords);

                for(let j=0; j < course[1].keywords.length; j++) {
                  console.log("in last for");
                  if (keywords[i] === course[1].keywords) {
                    courses.push(
                      <RecommendedCourse coursekey={course[0]} data={course[1]}>

                      </RecommendedCourse>
                    )
                  }
                }
              }
            }
          } 
        }
        return courses;
      }

      render() {
        return (

          <div class="container-fluid">
            <div class="row">
              <div class="col-lg-6" >{this.getCourses()}</div>
              <div class="col-lg-6" >{this.getCourses()}</div>
            </div>
          </div>

        )
      }
}


export default CourseRecommendor
